import { useState } from "react";
import "./styles.css";

export default function App() {
  const [flag, setFlag] = useState("Loading...");
  // make an browswer api request to https://wgg522pwivhvi5gqsn675gth3q0otdja.lambda-url.us-east-1.on.aws/626f6e to get the text from body tag
  const url = "https://wgg522pwivhvi5gqsn675gth3q0otdja.lambda-url.us-east-1.on.aws/626f6e";
  fetch(url)
    .then((response) => response.text())
    .then((data) => {
      // decode the base64 encoded text
      const decodedText = atob(data);
      setFlag(decodedText);
    });


  return <div className="App">{flag}</div>;
}
