import { useEffect, useState } from "react";
import "./styles.css";

export default function App() {
  const [flag, setFlag] = useState("Loading...");
  const [displayedFlag, setDisplayedFlag] = useState([]);

  useEffect(() => {
    console.log("flag", flag);
    if (flag == "Loading..."){
      return;
    }
    elsehandleAnim();
  }, [flag]);

  const handleAnim = () => {
    const count = 0;
    const total = flag.length;
    const interval = setInterval(() => {
      let temp = [...displayedFlag];
      temp.shift();
      setDisplayedFlag(temp);
    }, 1000);
    setDisplayedFlag(flag.split("")[0]);
  };





  // make an browswer api request to https://wgg522pwivhvi5gqsn675gth3q0otdja.lambda-url.us-east-1.on.aws/626f6e to get the text from body tag
  const url = "https://wgg522pwivhvi5gqsn675gth3q0otdja.lambda-url.us-east-1.on.aws/626f6e";
  fetch(url)
    .then((response) => response.text())
    .then((data) => {
      // console.log(data);
      setFlag(data);
    });


  return <div className="App">{flag}</div>;
}
